mapper.select:
select count(*) from DOLPHIN_CONTRACT where (outlet_no = #{accountNo} or #{accountNo} is null) and (po_no =#{poNo} or #{poNo} is null )
 and CONVERT(BIGINT,DATEDIFF(MI,'1970-01-01 00:00:00.000', last_modified_time)) * 60000 + DATEPART(S, last_modified_time) * 1000 + DATEPART(MS, last_modified_time) >  #{startTime}
 and CONVERT(BIGINT,DATEDIFF(MI,'1970-01-01 00:00:00.000', last_modified_time)) * 60000 + DATEPART(S, last_modified_time) * 1000 + DATEPART(MS, last_modified_time)  <  #{endTime}

select count(*) from DOLPHIN_CONTRACT_DISCOUNT where ( contract_number =#{contractNumber} or #{contractNumber} is null)
AND
CONVERT(BIGINT,DATEDIFF(MI,'1970-01-01 00:00:00.000', last_modified_time)) * 60000 + DATEPART(S, last_modified_time) * 1000 + DATEPART(MS, last_modified_time) >  #{startTime}
 and CONVERT(BIGINT,DATEDIFF(MI,'1970-01-01 00:00:00.000', last_modified_time)) * 60000 + DATEPART(S, last_modified_time) * 1000 + DATEPART(MS, last_modified_time)  <  #{endTime}
